package com.jugheadzhou.base.controller;import com.jugheadzhou.base.service.ClientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;/** * @description: * @author: aishu * @create: 2019-10-17 18:05 */@RestController@RequestMapping("hello")public class HelloController {    @Autowired    private ClientService clientService;    @GetMapping("say")    public String say(String user){        return clientService.say(user);    }}